export function addCity(city) {
  return {
    type: 'ADD_CITY',
    payload: city
  };
}

export function deleteCity(id) {
  return {
    type: 'DELETE_CITY',
    payload: id
  };
}

export function getCity(cities) {
  return {
    type: 'GET_CITY',
    payload: cities
  }
}

export function fetchAddedCitiesSuccess(response, city) {
  return {
    type: 'FETCH_ADDED_CITY_SUCCESS',
    payload: {
      response,
      city
    }
  }
}

export function fetchAddedCitiesError(error, id) {
  return {
    type: 'FETCH_ADDED_CITY_ERROR',
    payload: {
      error,
      id
    }
  }
}

export function getWeatherByCity(city) {
 return function(dispatch) {
   console.log(city.name);
    fetch(`weather?city=${city.name}`)
      .then(response => {
        response.json()
          .then(json => {
            if (response.ok) {
              dispatch(fetchAddedCitiesSuccess(json, city.name));
            } else {
              let error = json.message;
              dispatch(fetchAddedCitiesError(error, city.id));
            }
          });
      },
      error => dispatch(fetchAddedCitiesError(error, city.id)))
  }
}
