import React from "react";
import { connect } from "react-redux";
import Weather from '../Weather/Weather';
import { addCity, deleteCity } from '../../actions/addedCitiesAction';
import { getWeatherByCity } from '../../fetch/getWeatherByCity';


class AddedCities extends React.Component {
  render() {
    return (
      <div className="favorites">
      <div className="CitiesPanel">
        <div className="CitiesText">Favorites</div>
        <form className="add_city" onSubmit={(e) => this.addNewCity(e)}>
          <input className="CitiesInput" type="text" name="cityName" placeholder="City name" required />
          <input className="CitiesButton" type="submit" value="Add city"/>
        </form>
      </div>
        {this.props.error && <div className="error">Error: {this.props.error}</div>}
        <div className="forecasts">
          {
            [...this.props.favorites.entries()].map((entry) => {
              return (
                <Weather
                  key={entry[0]}
                  onFetch={() => this.props.getWeatherByCity(entry[0])}
                  onDelete={() => this.props.deleteCity(entry[0])}
                  data={entry[1]}/>
              );
            })
          }
        </div>
      </div>
    );
  }

  addNewCity(e) {
    e.preventDefault();
    this.props.addCity(e.currentTarget.elements.cityName.value);
  }
}


function mapStateToProps(state) {
  return {
    favorites: state.fav.favorites,
    error: state.fav.error
  };
}

function mapDispatchToProps(dispatch) {
  return {
    addCity: (cityName) => {
      dispatch(addCity(cityName));
    },

    deleteCity: (cityName) => {
      dispatch(deleteCity(cityName));
    },

    getWeatherByCity: (cityName) => {
      dispatch(getWeatherByCity(cityName));
    }
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(AddedCities);
