export function addCity(cityName) {
  return {
    type: 'ADD_CITY',
    payload: cityName
  };
}

export function deleteCity(cityName) {
  return {
    type: 'DELETE_CITY',
    payload: cityName
  };
}

export function getSuccessResponse(response, cityName) {
  return {
    type: 'GET_SUCCESS_RESPONSEE',
    payload: {
      response,
      cityName
    }
  }
}

export function getErrorResponse(error, cityName) {
  return {
    type: 'GET_ERROR_RESPONSEE',
    payload: {
      error,
      cityName
    }
  }
}
